import React, { useState } from 'react';

const CryptoCurrency = () => {
  const [currency1, setCurrency1] = useState('BTC');
  const [currency2, setCurrency2] = useState('USD');
  const [value1, setValue1] = useState('');
  const [convertedValue, setConvertedValue] = useState('');
  const [error, setError] = useState(0);

  const convert = async () => {
    if(currency1 !== '' && currency2 !== '' && value1 !== ''){
      try {
        const response = await fetch(`http://localhost:5500/api/convert/cryptotocurrency/${currency1}/${currency2}/${value1}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error('Failed to convert. Please try again.');
        }

        const result = await response.json();
        setConvertedValue(result);
      } catch (error) {
        console.error('Error converting:', error.message);
        // Handle error as needed, e.g., display an error message to the user
      }
    }else{
      setConvertedValue("Please input all fields");
    }

  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Currency Converter</h1>
      <div className="row">
        <div className="col-md-6">
          <label>Crypto Currency</label>
          <select className="form-control" value={currency1} onChange={(e) => setCurrency1(e.target.value)}>
            <option value="BTC">Bitcoin</option>
            <option value="ETH">Ethereum</option>
            <option value="DOGE">Dogecoin</option>
            {/* Add more options as needed */}
          </select>
          <br />
        </div>
        <div className="col-md-6">
          <label>Country Currency</label>
          <select className="form-control" value={currency1} onChange={(e) => setCurrency2(e.target.value)}>
            <option value="USD">US Dollar</option>
            <option value="GBP">Pound Sterling</option>
            <option value="GBP">Indian Rupee</option>
            {/* Add more options as needed */}
          </select>
          <br />
          </div>
          <div className="col-md-6 mx-auto">
            <label>Quantity</label>
            <input
              type="text"
              className="form-control"
              value={value1}
              placeholder='Enter amount'
              onChange={(e) => setValue1(e.target.value)}
            />
        </div>
      </div>
      <br />
      <div className="d-flex justify-content-center">
        <button className="btn btn-primary mx-auto" onClick={convert}>
          Convert
        </button>
      </div>
      <br/>
      <div className="d-flex justify-content-center">
        {convertedValue.amount}
    </div>
    </div>
  );
};

export default CryptoCurrency;
