import React, { useState } from 'react';

const ConverterApp = () => {
  const [unit1, setUnit1] = useState('');
  const [unit2, setUnit2] = useState('');
  const [value1, setValue1] = useState('');
  const [convertedValue, setConvertedValue] = useState('');

  const convert = () => {
    // Add your conversion logic here
    // For simplicity, this example assumes a 1:1 conversion ratio
    const converted = parseFloat(value1) || 0;
    setConvertedValue(converted + ' ' + unit2);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Currency Converter</h1>
      <div className="row">
        <div className="col-md-6">
          <label>Crypto Currency</label>
          <select className="form-control" value={unit1} onChange={(e) => setUnit1(e.target.value)}>
            <option value="BTC">Bitcoin</option>
            <option value="ETH">Ethereum</option>
            <option value="DOGE">Dogecoin</option>
            {/* Add more options as needed */}
          </select>
          <br />
        </div>
        <div className="col-md-6">
          <label>Country Currency</label>
          <select className="form-control" value={unit2} onChange={(e) => setUnit2(e.target.value)}>
            <option value="unit2Option1">Unit 2 Option 1</option>
            <option value="unit2Option2">Unit 2 Option 2</option>
            {/* Add more options as needed */}
          </select>
          <br />
          </div>
          <div className="col-md-6">
            <label>Amount</label>
            <input
              type="text"
              className="form-control"
              value={value1}
              onChange={(e) => setValue1(e.target.value)}
            />
        </div>
      </div>
      <br />
      <button className="btn btn-primary" onClick={convert}>
        Convert
      </button>
    </div>
  );
};

export default ConverterApp;
