import React, { useState } from 'react';

const CryptoCurrency = () => {
  const [unit1, setUnit1] = useState('');
  const [unit2, setUnit2] = useState('');
  const [value1, setValue1] = useState('');
  const [convertedValue, setConvertedValue] = useState('');

  const convert = async () => {
    try {
      const response = await fetch('http://your-backend-service-url/api/convert', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          unit1,
          unit2,
          value1: parseFloat(value1) || 0,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to convert. Please try again.');
      }

      const result = await response.json();
      setConvertedValue(result.convertedValue);
    } catch (error) {
      console.error('Error converting:', error.message);
      // Handle error as needed, e.g., display an error message to the user
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Currency Converter</h1>
      <div className="row">
        <div className="col-md-6">
          <label>Crypto Currency</label>
          <select className="form-control" value={unit1} onChange={(e) => setUnit1(e.target.value)}>
            <option value="BTC">Bitcoin</option>
            <option value="ETH">Ethereum</option>
            <option value="DOGE">Dogecoin</option>
            {/* Add more options as needed */}
          </select>
          <br />
        </div>
        <div className="col-md-6">
          <label>Country Currency</label>
          <select className="form-control" value={unit2} onChange={(e) => setUnit2(e.target.value)}>
            <option value="USD">US Dollar</option>
            <option value="GBP">Pound Sterling</option>
            <option value="GBP">Indian Rupee</option>
            {/* Add more options as needed */}
          </select>
          <br />
          </div>
          <div className="col-md-6 mx-auto">
            <label>Amount</label>
            <input
              type="text"
              className="form-control"
              value={value1}
              placeholder='Enter amount'
              onChange={(e) => setValue1(e.target.value)}
            />
        </div>
      </div>
      <br />
      <div className="d-flex justify-content-center">
        <button className="btn btn-primary mx-auto" onClick={convert}>
          Convert
        </button>
      </div>
    </div>
  );
};

export default CryptoCurrency;
