import React, { useState } from 'react';

const ConverterApp = () => {
  const [unit1, setUnit1] = useState(''); // Selected unit from dropdown 1
  const [unit2, setUnit2] = useState(''); // Selected unit from dropdown 2
  const [value1, setValue1] = useState(''); // Value entered in textbox 1
  const [convertedValue, setConvertedValue] = useState(''); // Converted value to display

  const convert = () => {
    // Add your conversion logic here
    // For simplicity, this example assumes a 1:1 conversion ratio
    const converted = parseFloat(value1) || 0;
    setConvertedValue(converted + ' ' + unit2);
  };

  return (
    <div>
      <h1>Converter App</h1>
      <div>
        <label>
          Crypto Currency
          <select value={unit1} onChange={(e) => setUnit1(e.target.value)}>
            <option value="unit1Option1">Unit 1 Option 1</option>
            <option value="unit1Option2">Unit 1 Option 2</option>
            {/* Add more options as needed */}
          </select>
        </label>
        <br />
        <label>
          Enter Value 1:
          <input
            type="text"
            value={value1}
            onChange={(e) => setValue1(e.target.value)}
          />
        </label>
      </div>
      <br />
      <div>
        <label>
          Select Unit 2:
          <select value={unit2} onChange={(e) => setUnit2(e.target.value)}>
            <option value="unit2Option1">Unit 2 Option 1</option>
            <option value="unit2Option2">Unit 2 Option 2</option>
            {/* Add more options as needed */}
          </select>
        </label>
        <br />
        <label>
          Converted Value:
          <input type="text" value={convertedValue} readOnly />
        </label>
      </div>
      <br />
      <button onClick={convert}>Convert</button>
    </div>
  );
};

export default ConverterApp;
